import java.util.List;import org.antlr.v4.runtime.CharStreams;import org.antlr.v4.runtime.CommonTokenStream;import org.antlr.v4.runtime.Token;public class Tokenizer {    public static List<Token> tokenize (String s){        RddLexer rddLexer = new RddLexer(CharStreams.fromString(s));        CommonTokenStream tokens = new CommonTokenStream(rddLexer);        return tokens.getTokens();    }}